INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2016-12-22 14:32:53', '2016-12-22 12:32:53', 'Ласкаво просимо до WordPress. Це ваш перший запис. Відредагуйте або видаліть його, тоді почніть писати!', 'Привіт світ!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2016-12-22 14:32:53', '2016-12-22 12:32:53', '', 0, 'http://centralmedia/?p=1', 0, 'post', '', 1),
(2, 1, '2016-12-22 14:32:53', '2016-12-22 12:32:53', 'Це приклад сторінки. Вона відрізняється від записів блогу, бо залишається на одному місці і відображається в меню сайту (у більшості тем). Більшість людей починають зі сторінки, яка представляє їх потенційним відвідувачам сайту. На ній можна написати щось на кшталт цього:\n\n<blockquote>Привіт! Вдень я кур''єр, а ввечері - актор що подає надії. А це мій блог. Я живу у Львові, люблю свого чудового собаку Бровка та карпатський чай. (І ще потрапляти під дощ.)</blockquote>\n\n...або цього:\n\n<blockquote>Компанія "Штучки АБВ" була заснована в 1992 році, і з тих пір забезпечує людей відмінними штучками. Компанія знаходиться в Стрию, має штат з понад 2000 співробітників і приносить багато користі жителям Стрия.</blockquote>\n\nЯк новому користувачу WordPress, вам слід перейти до <a href="http://centralmedia/wp-admin/">вашої майстерні</a>, щоб видалити цю сторінку і створити нові сторінки для вашого вмісту. Насолоджуйтесь!', 'Зразок сторінки', '', 'trash', 'closed', 'open', '', 'sample-page__trashed', '', '', '2017-01-03 22:29:36', '2017-01-03 20:29:36', '', 0, 'http://centralmedia/?page_id=2', 0, 'page', '', 0),
(4, 1, '2016-12-22 15:14:37', '2016-12-22 13:14:37', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum', 'Пожежу біля Лісової ліквідували', '', 'publish', 'open', 'open', '', 'news-1', '', '', '2016-12-25 17:34:57', '2016-12-25 15:34:57', '', 0, 'http://centralmedia/?post_type=news&#038;p=4', 0, 'news', '', 0),
(5, 1, '2016-12-22 15:14:37', '2016-12-22 13:14:37', 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum', 'News 1', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2016-12-22 15:14:37', '2016-12-22 13:14:37', '', 4, 'http://centralmedia/2016/12/22/4-revision-v1/', 0, 'revision', '', 0),
